document.querySelectorAll(".img-product").forEach((el=>el.addEventListener("click",(()=>document.getElementById("img-product").style.backgroundImage=el.style.backgroundImage))));document.getElementById("plus")?.addEventListener("click",(()=>{let price_total=document.getElementById("order_product_form_price_total");let step=price_total.dataset.step*1;let result=price_total.value*1+step;let max=price_total.dataset.max*1;if(result<=max){document.getElementById("order_product_form_price_total").value=result}}));document.getElementById("minus")?.addEventListener("click",(()=>{let price_total=document.getElementById("order_product_form_price_total");let step=price_total.dataset.step*1;let result=price_total.value*1;if(result>1){result=result-step;if(result<=0){return}document.getElementById("order_product_form_price_total").value=result}}));const product_price_total=document.getElementById("order_product_form_price_total");product_price_total.addEventListener("input",orderProductCounter.debounce(500));function orderProductCounter(){let step=this.dataset.step*1;let total=this.value*1;if(total===0){total=step;this.value=step}let max=this.dataset.max*1;let remainder=this.value%step;if(remainder!==0){if(total<step){this.value=step}if(total>step){this.value=total-remainder}}}