executeFunc(share);function share(){const shareButton=document.getElementById("shareButton");if(!shareButton){console.error("Кнопка не найдена!");return}if(navigator.share){shareButton.addEventListener("click",(function(){navigator.share({title:document.title,text:"Посмотри это!",url:window.location.href.split("?")[0]+"?share="+this.getAttribute("data-shared")}).catch((error=>console.error("Ошибка: ",error)))}))}else{shareButton.addEventListener("click",(function(){const url=window.location.href.split("?")[0]+"?share="+this.getAttribute("data-shared");navigator.clipboard.writeText(url).then((()=>{let toastMessage=JSON.stringify({type:"success",header:"Копирование",message:"Ссылка успешно скопирована в буфер обмена"});createToast(JSON.parse(toastMessage))})).catch((error=>console.error("Ошибка при копировании: ",error)))}))}return true}